package eu.europa.esig.dss.cades;

import eu.europa.esig.dss.spi.DSSASN1Utils;
import eu.europa.esig.dss.spi.DSSUtils;
import eu.europa.esig.dss.spi.OID;
import eu.europa.esig.dss.utils.Utils;
import org.bouncycastle.asn1.ASN1EncodableVector;
import org.bouncycastle.asn1.ASN1GeneralizedTime;
import org.bouncycastle.asn1.ASN1ObjectIdentifier;
import org.bouncycastle.asn1.ASN1Primitive;
import org.bouncycastle.asn1.ASN1UTCTime;
import org.bouncycastle.asn1.DERSet;
import org.bouncycastle.asn1.cms.Attribute;
import org.bouncycastle.asn1.cms.AttributeTable;
import org.bouncycastle.asn1.pkcs.PKCSObjectIdentifiers;
import org.bouncycastle.asn1.x509.Time;
import org.bouncycastle.cms.CMSSignedData;
import org.bouncycastle.tsp.TimeStampToken;
import org.junit.jupiter.api.Test;

import java.util.Arrays;
import java.util.Date;
import java.util.Hashtable;
import java.util.List;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertNotEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertTrue;

class CMSUtilsTest {
	
	@Test
	void cmsSignedDataEqualTest() throws Exception {
		String tstBase64 = "";
		byte[] tstBinaries = Utils.fromBase64(tstBase64);
		ASN1Primitive asn1Primitive = DSSASN1Utils.toASN1Primitive(tstBinaries);
		Attribute atstV3Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV3, new DERSet(asn1Primitive));
		
		CMSSignedData cmsSignedData = CMSUtils.getCMSSignedData(atstV3Attibute);
		assertFalse(Arrays.equals(atstV3Attibute.getEncoded(), cmsSignedData.getEncoded()));
		assertTrue(CMSUtils.isCMSSignedDataEqual(cmsSignedData, CMSUtils.getCMSSignedData(atstV3Attibute)));
		
		String berEncodedTST = "";
		assertFalse(CMSUtils.isCMSSignedDataEqual(cmsSignedData, new CMSSignedData(Utils.fromBase64(berEncodedTST))));
	}

	@Test
	void readSigningDateTest() {
		Date date = DSSUtils.getUtcDate(1900, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new Time(date)));
		date = DSSUtils.getUtcDate(1950, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new Time(date)));
		date = DSSUtils.getUtcDate(2000, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new Time(date)));
		date = DSSUtils.getUtcDate(2050, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new Time(date)));
		date = DSSUtils.getUtcDate(2100, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new Time(date)));

		date = DSSUtils.getUtcDate(1900, 0, 1);
		assertNotEquals(date, CMSUtils.readSigningDate(new ASN1UTCTime(date)));
		date = DSSUtils.getUtcDate(1950, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new ASN1UTCTime(date)));
		date = DSSUtils.getUtcDate(2000, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new ASN1UTCTime(date)));
		date = DSSUtils.getUtcDate(2050, 0, 1);
		assertNotEquals(date, CMSUtils.readSigningDate(new ASN1UTCTime(date)));
		date = DSSUtils.getUtcDate(2100, 0, 1);
		assertNotEquals(date, CMSUtils.readSigningDate(new ASN1UTCTime(date)));

		date = DSSUtils.getUtcDate(1900, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new ASN1GeneralizedTime(date)));
		date = DSSUtils.getUtcDate(1950, 0, 1);
		assertNotEquals(date, CMSUtils.readSigningDate(new ASN1GeneralizedTime(date)));
		date = DSSUtils.getUtcDate(2000, 0, 1);
		assertNotEquals(date, CMSUtils.readSigningDate(new ASN1GeneralizedTime(date)));
		date = DSSUtils.getUtcDate(2050, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new ASN1GeneralizedTime(date)));
		date = DSSUtils.getUtcDate(2100, 0, 1);
		assertEquals(date, CMSUtils.readSigningDate(new ASN1GeneralizedTime(date)));
	}

	@Test
	void isEmpty() {
		assertTrue(CMSUtils.isEmpty(null));
		assertTrue(CMSUtils.isEmpty(new AttributeTable(new Hashtable<>())));
		Hashtable<ASN1ObjectIdentifier, Object> nonEmpty = new Hashtable<>();
		nonEmpty.put(new ASN1ObjectIdentifier("1.2.3.4.5"), 4);
		assertFalse(CMSUtils.isEmpty(new AttributeTable(nonEmpty)));
	}

	@Test
	void emptyIfNull() {
		assertNotNull(CMSUtils.emptyIfNull(null));

		Hashtable<ASN1ObjectIdentifier, Object> nonEmpty = new Hashtable<>();
		nonEmpty.put(new ASN1ObjectIdentifier("1.2.3.4.5"), 4);
		AttributeTable attributeTable = new AttributeTable(nonEmpty);

		AttributeTable emptyIfNull = CMSUtils.emptyIfNull(attributeTable);
		assertNotNull(emptyIfNull);
		assertEquals(attributeTable, emptyIfNull);
	}

	@Test
	void isArchiveTimeStampTokenTest() {
		String atstV2 = "";
		ASN1Primitive asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(atstV2));
		Attribute atstV2Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV2, new DERSet(asn1Primitive));
		assertTrue(CMSUtils.isArchiveTimeStampToken(atstV2Attibute));

		String atstV3 = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(atstV3));
		Attribute atstV3Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV3, new DERSet(asn1Primitive));
		assertTrue(CMSUtils.isArchiveTimeStampToken(atstV3Attibute));

		String sigTst = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(sigTst));
		Attribute sigTstsAttibute = new Attribute(PKCSObjectIdentifiers.id_aa_signatureTimeStampToken, new DERSet(asn1Primitive));
		assertFalse(CMSUtils.isArchiveTimeStampToken(sigTstsAttibute));

		String certValues = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(certValues));
		Attribute certValuesAttibute = new Attribute(PKCSObjectIdentifiers.id_aa_ets_certValues, new DERSet(asn1Primitive));
		assertFalse(CMSUtils.isArchiveTimeStampToken(certValuesAttibute));
	}

	@Test
	void findArchiveTimeStampTokensTest() {
		ASN1EncodableVector asn1EncodableVector = new ASN1EncodableVector();

		String atstV2 = "";
		ASN1Primitive asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(atstV2));
		Attribute atstV2Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV2, new DERSet(asn1Primitive));
		asn1EncodableVector.add(atstV2Attibute);

		String malformedAtstV2 = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(malformedAtstV2));
		Attribute malformedAtstV2Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV2, new DERSet(asn1Primitive));
		asn1EncodableVector.add(malformedAtstV2Attibute);

		String atstV3 = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(atstV3));
		Attribute atstV3Attibute = new Attribute(OID.id_aa_ets_archiveTimestampV3, new DERSet(asn1Primitive));
		asn1EncodableVector.add(atstV3Attibute);

		String sigTst = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(sigTst));
		Attribute sigTstsAttibute = new Attribute(PKCSObjectIdentifiers.id_aa_signatureTimeStampToken, new DERSet(asn1Primitive));
		asn1EncodableVector.add(sigTstsAttibute);

		String certValues = "";
		asn1Primitive = DSSASN1Utils.toASN1Primitive(Utils.fromBase64(certValues));
		Attribute certValuesAttibute = new Attribute(PKCSObjectIdentifiers.id_aa_ets_certValues, new DERSet(asn1Primitive));
		asn1EncodableVector.add(certValuesAttibute);

		AttributeTable attributeTable = new AttributeTable(asn1EncodableVector);
		List<TimeStampToken> timeStampTokens = CMSUtils.findArchiveTimeStampTokens(attributeTable);

		assertEquals(2, timeStampTokens.size());
	}

}
