	@Test
	public void incorrectTimestamp() throws Exception {
		Exception exception = assertThrows(CMSException.class, () -> new TimestampToken(new byte[] { 1, 2, 3 }, TimestampType.ARCHIVE_TIMESTAMP));
		assertEquals("IOException reading content.", exception.getMessage());
	}

	@Test
	public void disig() throws Exception {

		DSSDocument tst = new FileDocument("src/test/resources/disig.tst");

		try (InputStream fis = tst.openStream()) {
			byte[] byteArray = Utils.toByteArray(fis);
			TimestampToken token = new TimestampToken(byteArray, TimestampType.ARCHIVE_TIMESTAMP);
			TimestampCertificateSource certificateSource = token.getCertificateSource();
			List<CertificateToken> certificates = certificateSource.getCertificates();
			assertEquals(2, certificates.size());
			assertNotEquals(certificates.get(0), certificates.get(1));
			assertTrue(certificates.get(0).isEquivalent(certificates.get(1)));

			List<CertificateRef> refs = certificateSource.getSigningCertificateRefs();
			assertEquals(2, refs.size());
			assertNotEquals(refs.get(0), refs.get(1));

			Set<CertificateToken> sigCertValues = certificateSource.getSigningCertificates();
			assertEquals(1, sigCertValues.size());

			for (CertificateRef certificateRef : refs) {
				for (CertificateToken sigCertValue : sigCertValues) {
					assertArrayEquals(sigCertValue.getDigest(certificateRef.getCertDigest().getAlgorithm()), certificateRef.getCertDigest().getValue());
				}
			}
			for (CertificateToken certificateToken : sigCertValues) {
				assertTrue(token.isSignedBy(certificateToken));
			}

			// no Trust anchor
			SignedDocumentValidator validator = SignedDocumentValidator.fromDocument(tst);
			CommonCertificateVerifier certificateVerifier = new CommonCertificateVerifier();
			validator.setCertificateVerifier(certificateVerifier);
			Reports reports = validator.validateDocument();

			List<TimestampWrapper> timestampList = reports.getDiagnosticData().getTimestampList();
			assertEquals(1, timestampList.size());

			TimestampWrapper timestampWrapper = timestampList.get(0);
			assertTrue(timestampWrapper.isSignatureIntact());

			String b1 = "";
			String b2 = "";

			// 1st TSU as Trust anchor
			validator = SignedDocumentValidator.fromDocument(tst);
			CommonTrustedCertificateSource trustedCertSource = new CommonTrustedCertificateSource();
			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b1));
//			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b2));
			certificateVerifier.setTrustedCertSources(trustedCertSource);

			validator.setCertificateVerifier(certificateVerifier);
			reports = validator.validateDocument();

			timestampList = reports.getDiagnosticData().getTimestampList();
			assertEquals(1, timestampList.size());

			timestampWrapper = timestampList.get(0);
			assertTrue(timestampWrapper.isSignatureIntact());

			// 2nd TSU as Trust anchor
			validator = SignedDocumentValidator.fromDocument(tst);
			trustedCertSource = new CommonTrustedCertificateSource();
//			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b1));
			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b2));
			certificateVerifier.setTrustedCertSources(trustedCertSource);

			validator.setCertificateVerifier(certificateVerifier);
			reports = validator.validateDocument();

			timestampList = reports.getDiagnosticData().getTimestampList();
			assertEquals(1, timestampList.size());

			timestampWrapper = timestampList.get(0);
			assertTrue(timestampWrapper.isSignatureIntact());

			// both TSU as Trust anchor
			validator = SignedDocumentValidator.fromDocument(tst);
			trustedCertSource = new CommonTrustedCertificateSource();
			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b1));
			trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(b2));
			certificateVerifier.setTrustedCertSources(trustedCertSource);

			validator.setCertificateVerifier(certificateVerifier);
			reports = validator.validateDocument();

			timestampList = reports.getDiagnosticData().getTimestampList();
			assertEquals(1, timestampList.size());

			timestampWrapper = timestampList.get(0);
			assertTrue(timestampWrapper.isSignatureIntact());

		}
	}

	@Test
	public void correctToken() throws Exception {
		CertificateToken wrongToken = DSSUtils.loadCertificate(new File("src/test/resources/certificates/ec.europa.eu.crt"));

		try (FileInputStream fis = new FileInputStream("src/test/resources/archive_timestamp.tst")) {
			byte[] byteArray = Utils.toByteArray(fis);
			TimestampToken token = new TimestampToken(byteArray, TimestampType.ARCHIVE_TIMESTAMP);
			assertNotNull(token);
			LOG.info(token.toString());

			assertFalse(token.isSignedBy(wrongToken));
			assertNotNull(token.getGenerationTime());
			assertNotNull(token.getAbbreviation());
			assertTrue(Utils.isCollectionNotEmpty(token.getCertificates()));
			assertEquals(TimestampType.ARCHIVE_TIMESTAMP, token.getTimeStampType());
			assertNotNull(token.getMessageImprint());
			assertEquals(DigestAlgorithm.SHA256, token.getMessageImprint().getAlgorithm());
			assertTrue(Utils.isArrayNotEmpty(token.getMessageImprint().getValue()));
			assertNull(token.getSignatureAlgorithm());

			List<CertificateToken> tstCerts = token.getCertificates();
			for (CertificateToken certificateToken : tstCerts) {
				if (token.isSignedBy(certificateToken)) {
					break;
				}
			}

			assertNotNull(token.getPublicKeyOfTheSigner());

			assertNotNull(token.getSignatureAlgorithm());
			assertEquals(SignatureAlgorithm.RSA_SHA256, token.getSignatureAlgorithm());
			assertFalse(token.isSelfSigned());

			assertFalse(token.matchData((DSSDocument) null));

			assertFalse(token.matchData(new InMemoryDocument(new byte[] { 1, 2, 3 })));
			assertTrue(token.isMessageImprintDataFound());
			assertFalse(token.isMessageImprintDataIntact());

			DSSDocument timestampedData = new InMemoryDocument(Utils.fromBase64(TIMESTAMPED_DATA_B64));
			assertTrue(token.matchData(timestampedData));

			DigestDocument digestDoc = new DigestDocument(DigestAlgorithm.SHA256, timestampedData.getDigestValue(DigestAlgorithm.SHA256));
			assertTrue(token.matchData(digestDoc));

			// DSS-1906
			assertTrue(token.matchData(timestampedData.getDigestValue(DigestAlgorithm.SHA256)));

			assertTrue(token.isMessageImprintDataFound());
			assertTrue(token.isMessageImprintDataIntact());

			byte[] encoded = token.getEncoded();
			TimeStampToken tst = new TimeStampToken(new CMSSignedData(encoded));
			assertNotNull(tst);
		}
	}

	@Test
	public void eeTSTwithCerts() throws Exception {
		// request has been done with certReq = true
		// the token includes the certificate chain
		String base64TST = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64TST), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);
		List<CertificateToken> certificates = timestampToken.getCertificates();
		assertTrue(Utils.isCollectionNotEmpty(certificates));
		for (CertificateToken certificateToken : certificates) {
			if (timestampToken.isSignedBy(certificateToken)) {
				break;
			}
		}
		assertTrue(timestampToken.isSignatureIntact());
		assertTrue(timestampToken.matchData(new InMemoryDocument("Hello world".getBytes())));
	}

	@Test
	public void eeTSTwithoutCerts() throws Exception {
		// request has been done with certReq = false
		String base64TST = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64TST), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);
		assertFalse(Utils.isCollectionNotEmpty(timestampToken.getCertificates()));
		assertFalse(timestampToken.isSignatureIntact());
		assertTrue(timestampToken.matchData(new InMemoryDocument("Hello world".getBytes())));
	}

	@Test
	public void eeTSTwithoutCertsAndCertSource() throws Exception {
		// request has been done with certReq = false
		String base64TST = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64TST), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);
		assertTrue(Utils.isCollectionEmpty(timestampToken.getCertificates()));
		assertFalse(timestampToken.isSignatureIntact());
		assertTrue(timestampToken.matchData(new InMemoryDocument("Hello world".getBytes())));

		String base64TsuCert = "";
		CommonCertificateVerifier certificateVerifier = new CommonCertificateVerifier();
		CertificateSource adjunctCertSource = new CommonCertificateSource();
		adjunctCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(base64TsuCert));
		certificateVerifier.setAdjunctCertSources(adjunctCertSource);

		SignatureValidationContext svc = new SignatureValidationContext();
		svc.initialize(certificateVerifier);
		svc.addTimestampTokenForVerification(timestampToken);
		svc.validate();

		Set<TimestampToken> timestamps = svc.getProcessedTimestamps();
		assertEquals(1, timestamps.size());
		TimestampToken validationResult = timestamps.iterator().next();
		assertTrue(validationResult.isSignatureIntact());
	}

	@Test
	public void cadesSignatureTimestampTest() throws Exception {
		String base64TST = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64TST), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);

		assertEquals(2, timestampToken.getCertificates().size());
		assertEquals(2, timestampToken.getCertificateRefs().size());

		TimestampCRLSource crlSource = timestampToken.getCRLSource();
		assertNotNull(crlSource);
		assertEquals(1, crlSource.getAllRevocationBinaries().size());
		assertEquals(1, crlSource.getAllRevocationReferences().size());
	}

	@Test
	public void cadesArchiveTimestampV2Test() throws Exception {
		String base64TST = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64TST), TimestampType.ARCHIVE_TIMESTAMP);
		assertNotNull(timestampToken);

		assertEquals(2, timestampToken.getCertificates().size());
		assertEquals(1, timestampToken.getCertificateRefs().size());

		TimestampCRLSource crlSource = timestampToken.getCRLSource();
		assertNotNull(crlSource);
		assertEquals(1, crlSource.getAllRevocationBinaries().size());
		assertEquals(0, crlSource.getAllRevocationReferences().size());

		TimestampOCSPSource ocspSource = timestampToken.getOCSPSource();
		assertNotNull(ocspSource);
		assertEquals(0, ocspSource.getAllRevocationBinaries().size());
		assertEquals(0, ocspSource.getAllRevocationReferences().size());
	}

	@Test
	public void timestampSigCert() throws Exception {
		// Token contains both signingCert and signingCertV2
		String base64 = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);

		List<CertificateToken> certificates = timestampToken.getCertificates();
		assertEquals(1, certificates.size());
		assertEquals(2, timestampToken.getCertificateRefs().size());

		CertificateToken potentialSigner = certificates.get(0);
		assertFalse(timestampToken.isSignedBy(potentialSigner));

		TimestampCRLSource crlSource = timestampToken.getCRLSource();
		assertNotNull(crlSource);
		assertEquals(0, crlSource.getAllRevocationBinaries().size());
		assertEquals(0, crlSource.getAllRevocationReferences().size());

		TimestampOCSPSource ocspSource = timestampToken.getOCSPSource();
		assertNotNull(ocspSource);
		assertEquals(0, ocspSource.getAllRevocationBinaries().size());
		assertEquals(0, ocspSource.getAllRevocationReferences().size());
	}

	@Test
	public void timestampWithEmbeddedCRL() throws Exception {

		DigestDocument wrongData = new DigestDocument(DigestAlgorithm.SHA512,
				"0ZK7TlaEAd6q3O0l3V4vYauEXE6OotQXiYbyoWNOfxLSbKVzsnj+8/TMVl4qeQQtP6QOiITIJLW+BgB5A5Pvag==");
		DigestDocument timestampedData = new DigestDocument(DigestAlgorithm.SHA512,
				"1TK7TlaEAd6q3O0l3V4vYauEXE6OotQXiYbyoWNOfxLSbKVzsnj+8/TMVl4qeQQtP6QOiITIJLW+BgB5A5Pvag==");

		String base64 = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);
		assertFalse(timestampToken.isSelfSigned());

		List<CertificateToken> certificates = timestampToken.getCertificates();
		assertEquals(1, certificates.size());
		CertificateToken sigCert = certificates.get(0);

		// duplicated
		TimestampCertificateSource certificateSource = timestampToken.getCertificateSource();
		assertEquals(1, certificateSource.getSignedDataCertificates().size());
		assertEquals(1, certificateSource.getCertificateValues().size());
		assertFalse(certificateSource.isAllSelfSigned());
		assertFalse(certificateSource.isTrusted(sigCert));
		assertTrue(certificateSource.isKnown(sigCert));

		assertTrue(timestampToken.isSignedBy(sigCert));
		assertFalse(timestampToken.matchData(wrongData));
		assertTrue(timestampToken.matchData(timestampedData));

		TimestampCRLSource crlSource = timestampToken.getCRLSource();
		assertNotNull(crlSource);
		assertEquals(1, crlSource.getAllRevocationBinaries().size());

		TimestampOCSPSource ocspSource = timestampToken.getOCSPSource();
		assertNotNull(ocspSource);
		assertEquals(0, ocspSource.getAllRevocationBinaries().size());
		assertEquals(0, ocspSource.getAllRevocationReferences().size());
	}

	@Test
	public void dss1913() throws TSPException, IOException, CMSException {
		CertificateToken tsa = DSSUtils.loadCertificateFromBase64EncodedString(
				"");

		// 1st tst

		String base64 = "";
		TimestampToken timestampToken = new TimestampToken(Utils.fromBase64(base64), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);

		Digest messageImprint = timestampToken.getMessageImprint();
		assertNotNull(messageImprint);
		assertEquals("13C05E933E4B709D056385D98566C39FAB3329518353B5DBE1A0F3633F6CF1E8", messageImprint.getHexValue());
		assertEquals(DigestAlgorithm.SHA256, messageImprint.getAlgorithm());

		assertTrue(timestampToken.isSignedBy(tsa));
		assertEquals(SignatureAlgorithm.RSA_SHA1, timestampToken.getSignatureAlgorithm());
		assertEquals(SignatureValidity.VALID, timestampToken.getSignatureValidity());

		// 2nd tst

		base64 = "";
		timestampToken = new TimestampToken(Utils.fromBase64(base64), TimestampType.SIGNATURE_TIMESTAMP);
		assertNotNull(timestampToken);

		messageImprint = timestampToken.getMessageImprint();
		assertNotNull(messageImprint);
		assertEquals("0967CF2DBA239290FDCA89C6520EE77B971C8DF3C997E7F5AC4C6536DA166767", messageImprint.getHexValue());
		assertEquals(DigestAlgorithm.SHA256, messageImprint.getAlgorithm());

		assertTrue(timestampToken.isSignedBy(tsa));
		assertEquals(SignatureAlgorithm.RSA_SHA1, timestampToken.getSignatureAlgorithm());
		assertEquals(SignatureValidity.VALID, timestampToken.getSignatureValidity());
	}
	
