	@Test
	public void testEdDSA() throws Exception {
		String b64 = "";
		TimestampToken token = new TimestampToken(Utils.fromBase64(b64), TimestampType.CONTENT_TIMESTAMP);
		assertNotNull(token);

		List<CertificateToken> certificates = token.getCertificates();
		assertEquals(2, certificates.size());

		CandidatesForSigningCertificate candidatesForSigningCertificate = token.getCandidatesForSigningCertificate();
		assertNotNull(candidatesForSigningCertificate);

		CertificateValidity theBestCandidate = candidatesForSigningCertificate.getTheBestCandidate();
		assertNotNull(theBestCandidate);

		CertificateToken certificateToken = theBestCandidate.getCertificateToken();
		assertTrue(token.isSignedBy(certificateToken));
		assertEquals(32, DSSPKUtils.getPublicKeySize(certificateToken.getPublicKey()));

		assertEquals(SignatureAlgorithm.ED25519, token.getSignatureAlgorithm());
		assertEquals(SignatureValidity.VALID, token.getSignatureValidity());
		byte[] digest = DSSUtils.digest(DigestAlgorithm.SHA1, "Hello world".getBytes());
		assertTrue(token.matchData(digest));
	}

