package eu.europa.esig.dss.pades.signature;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

import java.io.IOException;
import java.util.List;
import java.util.Map;

import org.junit.jupiter.api.Test;

import eu.europa.esig.dss.model.DSSDocument;
import eu.europa.esig.dss.model.InMemoryDocument;
import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.pades.PAdESSignatureParameters;
import eu.europa.esig.dss.pades.validation.PAdESSignature;
import eu.europa.esig.dss.pades.validation.PDFDocumentValidator;
import eu.europa.esig.dss.pdf.PdfDssDict;
import eu.europa.esig.dss.pdf.ServiceLoaderPdfObjFactory;
import eu.europa.esig.dss.spi.DSSUtils;
import eu.europa.esig.dss.spi.x509.CertificateSource;
import eu.europa.esig.dss.spi.x509.CommonTrustedCertificateSource;
import eu.europa.esig.dss.test.signature.PKIFactoryAccess;
import eu.europa.esig.dss.validation.AdvancedSignature;
import eu.europa.esig.dss.validation.CertificateVerifier;

public class BuildKnownObjectsTest extends PKIFactoryAccess {

	/**
	 * Duplicate streams
	 * 
	 * CRLs: 27 = 21
	 * 
	 * 28 = 22
	 * 
	 * Certificates: 20=26
	 * 
	 * @throws IOException
	 */

	@Test
	public void buildKnownObjects() throws IOException {

		DSSDocument dssDocument = new InMemoryDocument(
				getClass().getResourceAsStream("/validation/dss-1696/Test.signed_Certipost-2048-SHA512.extended.pdf"));
		PDFDocumentValidator validator = new PDFDocumentValidator(dssDocument);
		validator.setCertificateVerifier(getOfflineCertificateVerifier());

		// <</Type /DSS
		// /Certs [20 0 R 26 0 R 30 0 R] -> 20 30
		// /CRLs [21 0 R 22 0 R 27 0 R 28 0 R 29 0 R]>> -> 21 22 29

		List<AdvancedSignature> signatures = validator.getSignatures();
		assertEquals(1, signatures.size());

		PAdESSignature padesSignature = (PAdESSignature) signatures.get(0);
		PdfDssDict dssDictionary = padesSignature.getDssDictionary();
		assertEquals(3, dssDictionary.getCERTs().size());
		assertEquals(5, dssDictionary.getCRLs().size());

		CertificateVerifier certificateVerifier = getOfflineCertificateVerifier();
		certificateVerifier.setExceptionOnMissingRevocationData(false);
		certificateVerifier.setExceptionOnUncoveredPOE(false);

		CertificateSource trustedCertSource = new CommonTrustedCertificateSource();
		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));
		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));
		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));
		trustedCertSource.addCertificate(DSSUtils.loadCertificateFromBase64EncodedString(
				""));
		certificateVerifier.setTrustedCertSource(trustedCertSource);

		PAdESLevelBaselineLT lt = new PAdESLevelBaselineLT(getGoodTsa(), certificateVerifier, new ServiceLoaderPdfObjFactory());
		DSSDocument extendSignature = lt.extendSignatures(dssDocument, new PAdESSignatureParameters());
		
		validator = new PDFDocumentValidator(extendSignature);
		validator.setCertificateVerifier(getOfflineCertificateVerifier());

		signatures = validator.getSignatures();
		assertEquals(1, signatures.size());

		PAdESSignature pades = (PAdESSignature) signatures.get(0);

		dssDictionary = pades.getDssDictionary();
		Map<Long, byte[]> crlMap = dssDictionary.getCRLs();
		assertEquals(3, crlMap.size()); // we don't collect newer CRLS
		// original duplicates must be referenced
		assertNotNull(crlMap.get(21L));
		assertNotNull(crlMap.get(22L));
		assertNotNull(crlMap.get(29L));

		Map<Long, CertificateToken> certMap = dssDictionary.getCERTs();
		assertNotNull(certMap.get(20L));
		assertNotNull(certMap.get(30L));

	}

	@Override
	protected String getSigningAlias() {
		return null;
	}

}
