package eu.europa.esig.dss.spi.x509;

import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.spi.DSSUtils;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

class EquivalentCertificatesEntityTest {

	private CertificateToken c1 = DSSUtils.loadCertificateFromBase64EncodedString(
			"");
	private CertificateToken c2 = DSSUtils.loadCertificateFromBase64EncodedString(
			"");

	private CertificateToken wrongCert = DSSUtils.loadCertificateFromBase64EncodedString(
			"");

	@Test
	void test() {
		EquivalentCertificatesEntity cpe = new EquivalentCertificatesEntity(c1);
		final byte[] ski = cpe.getSki();
		assertNotNull(ski);

		cpe.addEquivalentCertificate(c1);
		cpe.addEquivalentCertificate(c2);
		assertEquals(2, cpe.getEquivalentCertificates().size());

		cpe.addEquivalentCertificate(wrongCert);
		assertEquals(2, cpe.getEquivalentCertificates().size());
		assertArrayEquals(ski, cpe.getSki());
	}

	@Test
	void testEquals() {
		CertificateSourceEntity cpe1 = new EquivalentCertificatesEntity(c1);
		CertificateSourceEntity cpe2 = new EquivalentCertificatesEntity(c2);
		CertificateSourceEntity cpeWrong = new EquivalentCertificatesEntity(wrongCert);

		assertEquals(cpe1, cpe2);
		assertNotEquals(cpe1, cpeWrong);
	}

}
