package eu.europa.esig.dss.tsl.dto.condition;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.util.Arrays;

import org.junit.jupiter.api.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.spi.DSSUtils;

public class ExtendedKeyUsageConditionTest {

	private static final Logger LOG = LoggerFactory.getLogger(ExtendedKeyUsageConditionTest.class);

	@Test
	public void test() {
		CertificateToken certificateOCSP = DSSUtils.loadCertificateFromBase64EncodedString(
				"");

		ExtendedKeyUsageCondition ekuc = new ExtendedKeyUsageCondition(Arrays.asList("1.2.3"));
		LOG.info(ekuc.toString());
		assertFalse(ekuc.check(certificateOCSP));

		ekuc = new ExtendedKeyUsageCondition(Arrays.asList("1.3.6.1.5.5.7.3.9"));
		LOG.info(ekuc.toString());
		assertTrue(ekuc.check(certificateOCSP));

		ekuc = new ExtendedKeyUsageCondition(Arrays.asList("1.3.6.1.5.5.7.3.9", "1.2.3"));
		LOG.info(ekuc.toString());
		assertFalse(ekuc.check(certificateOCSP));
	}

}
