package eu.europa.esig.dss.tsl.dto.condition;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

import org.junit.jupiter.api.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.spi.DSSUtils;

public class PolicyIdConditionTest {

	private static final Logger LOG = LoggerFactory.getLogger(PolicyIdConditionTest.class);

	@Test
	public void test1() {
		CertificateToken certificate = DSSUtils.loadCertificateFromBase64EncodedString(
				"");

		PolicyIdCondition c = new PolicyIdCondition("1.2.3");
		LOG.info(c.toString());
		assertFalse(c.check(certificate));

		PolicyIdCondition c2 = new PolicyIdCondition("1.3.6.1.4.1.10015.1.1.1.1");
		LOG.info(c2.toString());
		assertTrue(c2.check(certificate));
	}

}
