package eu.europa.esig.dss.xades.validation.dkcert;

import static org.junit.jupiter.api.Assertions.assertEquals;

import java.io.File;
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import eu.europa.esig.dss.model.DSSDocument;
import eu.europa.esig.dss.model.FileDocument;
import eu.europa.esig.dss.model.x509.CertificateToken;
import eu.europa.esig.dss.spi.DSSUtils;
import eu.europa.esig.dss.spi.client.http.DataLoader;
import eu.europa.esig.dss.spi.client.http.MemoryDataLoader;
import eu.europa.esig.dss.utils.Utils;
import eu.europa.esig.dss.spi.signature.AdvancedSignature;
import eu.europa.esig.dss.spi.SignatureCertificateSource;
import eu.europa.esig.dss.validation.executor.signature.DefaultSignatureProcessExecutor;
import eu.europa.esig.dss.xades.validation.AbstractXAdESTestValidation;

public class AbstractDKTestCertificate extends AbstractXAdESTestValidation {

	private static final String DATE_STRING = "2019-08-05";
	private static final String DATE_FORMAT = "yyyy-MM-dd";

	private static final String AIA_URI = "http://m.aia.ica02.trust2408.com/oces-issuing02-ca.cer";
	private static final String AIA_BASE64_CONTENT = "";

	protected static final CertificateToken PREVIOUS_SIG_CERT = DSSUtils.loadCertificateFromBase64EncodedString(
			"");
	protected static final CertificateToken WRONG = DSSUtils.loadCertificateFromBase64EncodedString(
			"");

	protected static final CertificateToken EXPECTED_SIG_CERT = DSSUtils.loadCertificateFromBase64EncodedString(
			"");

	protected static final CertificateToken AIA_CERT = DSSUtils.loadCertificateFromBase64EncodedString(AIA_BASE64_CONTENT);
	
	@Override
	protected DSSDocument getSignedDocument() {
		return new FileDocument(new File("src/test/resources/validation/dk_tl-sn21.xml"));
	}
	
	protected DataLoader getMemoryDataLoader() {
		Map<String, byte[]> dataMap = new HashMap<>();
		dataMap.put(AIA_URI, Utils.fromBase64(AIA_BASE64_CONTENT));
		return new MemoryDataLoader(dataMap);
	}

	protected DefaultSignatureProcessExecutor fixedTime() throws ParseException {
		DefaultSignatureProcessExecutor processExecutor = new DefaultSignatureProcessExecutor();
		SimpleDateFormat sdf = new SimpleDateFormat(DATE_FORMAT);
		Date currentTime = sdf.parse(DATE_STRING);
		processExecutor.setCurrentTime(currentTime);
		return processExecutor;
	}
	
	@Override
	protected void checkAdvancedSignatures(List<AdvancedSignature> signatures) {
		super.checkAdvancedSignatures(signatures);
		AdvancedSignature advancedSignature = signatures.get(0);
		SignatureCertificateSource certificateSource = advancedSignature.getCertificateSource();
		List<CertificateToken> certificates = certificateSource.getCertificates();
		assertEquals(1, certificates.size());
		assertEquals(PREVIOUS_SIG_CERT, certificates.get(0));
	}

}
